import React, { useState, useEffect } from 'react';
import {
  BrowserRouter as Router,
  Route
} from "react-router-dom";
//
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import './App.css';
//

import axios from 'axios';


import Home from './components/Home';
import Login from './components/Login';
import Registration from './components/Registration';
import SearchPatient from './components/nurse/SearchPatient';


//
function App() {
  const [type, setType] = useState('');

  //check if the user already logged-in
  const readCookie = async () => {
    try {
      console.log('--- in readCookie function ---');

      //
      const res = await axios.get('/read_cookie');
      // 
      if (res.data.type !== undefined) {
        setType(res.data.type);
        console.log(res.data.type)
      }
    } catch (e) {
      setType('');
      console.log(e);
    }
  };

  //runs the first time the view is rendered
  //to check if user is signed in
  useEffect(() => {
    readCookie();
  }, []); //only the first render

  return (
    <Router>
      <Navbar bg="light" expand="lg">
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link href="/home">Home</Nav.Link>

            {type === 'nurse'
            ?
            <Nav.Link href="/searchPatient">Search Patient {type}</Nav.Link>
            :
            <div></div>
            }

            {screen === 'auto'
            ?
            <Nav.Link href="/login">Login {screen}</Nav.Link>
            :
            <Nav.Link onClick={deleteCookie}>Logout {screen}</Nav.Link>
            }

          </Nav>
        </Navbar.Collapse>
      </Navbar>

      <div>
        <Route render={() => < Home />} path="/home" />
        <Route render={() => < Login />} path="/login" />
        <Route render={() => < Registration />} path="/registration" />
        <Route render={() => < SearchPatient />} path="/searchPatient" />

      </div>

    </Router>


  );
}

export default App;
