import React, { useState, useEffect } from 'react';
import { withRouter } from 'react-router-dom';
import axios from 'axios';
import homeImg from './home.png';
import Button from 'react-bootstrap/Button';

function Home(props)
{
    const [screen, setScreen] = useState('auth');
    const [fullName, setFullName] = useState('');

    //check if the user already logged-in
    const readCookie = async () => {
      try {
        console.log('--- in readCookie function ---');

        //
        const res = await axios.get('/read_cookie');
        // 
        if (res.data.screen !== undefined) {
          setScreen(res.data.screen);
          console.log(res.data.screen)

          console.log(res.data.fullName);
          setFullName(res.data.fullName);
        }
      } catch (e) {
        setScreen('auth');
        console.log(e);
      }
    };
    //runs the first time the view is rendered
    //to check if user is signed in
    useEffect(() => {
      readCookie();
    }, []); //only the first render

    return (
        <div>
            <div className="home">
                {/* <h1>HOME: {screen}</h1> */}

                <div className="homeLeft">
                <h2 className="fullName">Welcome, {fullName}</h2>
                {screen === 'nurse'
                    ? 
                    <div>
                        <h3>We will help you to monitor</h3>
                        <h3>your patients' daily activities!</h3>
                    </div>
                    :                 
                    <div>
                        <h3>We will help you to monitor</h3>
                        <h3>your daily activities!</h3>
                    </div>
                }
                </div>

                <div className="homeRight">
                    <img className="homeImg" src={homeImg} alt='home image'></img>
                </div>

            </div>

            <div className="Buttons">
            <Button className="ButtonSpace" variant="primary" >Login</Button>
            <Button className="ButtonSpace" variant="success" >Regester</Button>
            </div>
        </div>
    );

}

export default withRouter(Home);
