import React, { useState, useEffect } from 'react';
import { withRouter } from 'react-router-dom';
import axios from 'axios';
import Spinner from 'react-bootstrap/Spinner';

import Home from '../Home';

function SearchPatient(props) {
    const [screen, setScreen] = useState('auth');
    const [showLoading, setShowLoading] = useState(false);
    const apiUrl = "http://localhost:3000/searchPatient";
    const [search, setSearch] = useState({
       type: '',
       str: '' 
    });

    //check if the user already logged-in
    const readCookie = async () => {
        try {
            console.log('--- in readCookie function ---');

            //
            const res = await axios.get('/read_cookie');
            // 
            if (res.data.screen !== undefined) {
                setScreen(res.data.screen);
                console.log(res.data.screen)
            }
        } catch (e) {
            setScreen('auth');
            console.log(e);
        }
    };
    //runs the first time the view is rendered
    //to check if user is signed in
    useEffect(() => {
        readCookie();
    }, []); //only the first render


    const searchPatient = (e) => {
    };

    const onChange = (e) => {
        e.persist();
        setSearch({...search, [e.target.name]: e.target.value});
      }

    return (
        <div>
            {showLoading &&
                <Spinner animation="border" role="status">
                    <span className="sr-only">Loading...</span>
                </Spinner>
            }

            {screen === 'auth'
                ?
                <Home screen={screen} setScreen={setScreen} />
                : 
                <div>
                    <h2>Search Patient: {screen}</h2>

                    <form onSubmit={searchPatient}>

                        <select id="searchType" onChange={onChange}>
                            <option value="id">ID</option>
                            <option value="firstName">First Name</option>
                            <option value="lastName">Last Name</option>
                            <option value="email">Email Address</option>
                            <option value="dateOfBirth">Date Of Birth</option>
                        </select>

                        <input type="text" name="searchStr" id="searchStr" value="" onChange={onChange}/>
                        <button type="submit">Search</button>
                    </form>
                </div>
            }


        </div>
    );

}

export default withRouter(SearchPatient);
